    private void bIniciarServidorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bIniciarServidorActionPerformed
        bIniciarServidor.setEnabled(false);
        String log =  taLog.getText();
        new Thread(new StoragePivo(taLog, tDistribuicao)).start();
        log +="Piv√¥ Iniciado\n";
        new Thread(new Storage(Porta.PORTA2.valor, taLog)).start();
        log +="Storage na porta "+Porta.PORTA2.valor+" Iniciado\n";
        new Thread(new Storage(Porta.PORTA3.valor, taLog)).start();
        log +="Storage na porta "+Porta.PORTA3.valor+" Iniciado\n";
        new Thread(new Storage(Porta.PORTA4.valor, taLog)).start();
        log +="Storage na porta "+Porta.PORTA4.valor+" Iniciado\n";
        taLog.setText(log);
    }//GEN-LAST:event_bIniciarServidorActionPerformed

